--------------------------------------------------------------------------------------------------------------
-- ESTE STORE SOLO VERIFICA QUE LOS HORARIOS ESTEN DISPONIBLES PARA SER UTILIZADOS
-- NO VERIFICA SI EXISTEN TURNOS AGENDADOS
--------------------------------------------------------------------------------------------------------------
CREATE PROCEDURE [dbo].[JDP_TUR_SpHorariosInhablitados]
    @Unidad INT,
    @Servicio INT,
    @id_un_servicio INT,
    @FechaDesde DATETIME,
    @FechaHasta DATETIME,
    @cantidad INT OUTPUT
AS
DECLARE @VariableAleatoria INT
    = ABS(CHECKSUM(NEWID())),
        @Dias INT = ((DATEPART(mm, @FechaHasta) * 30) + DATEPART(dd, @FechaHasta))
                    - ((DATEPART(mm, @FechaDesde) * 30) + DATEPART(dd, @FechaDesde)),
        @Incremento INT = 0;
--Borro por las dudas
IF OBJECT_ID('tempdb..#HorariosBloqueados') IS NOT NULL
    DROP TABLE #HorariosBloqueados;

-- genero tabla temporal
CREATE TABLE #HorariosBloqueados
(
    Clave INT,
    Fecha DATETIME,
    DiaSemana INT
);

WHILE (@Incremento <= @Dias)
BEGIN
    INSERT INTO #HorariosBloqueados
    (
        Clave,
        Fecha,
        DiaSemana
    )
    VALUES
    (@VariableAleatoria, DATEADD(D, @Incremento, CAST(@FechaDesde AS DATE)),
     DATEPART(dw, DATEADD(D, @Incremento, CAST(@FechaDesde AS DATE))));
    SET @Incremento = @Incremento + 1;
END;

SET DATEFIRST 1;

-- Genero Resultado
---------------------------------------------------------------------
-- Este es el nuevo, no cuenta bien cuando cambia de dia y
-- los dias no tienen las 24 hs habilitadas
---------------------------------------------------------------------

SELECT @cantidad = COUNT(*)
FROM
(
    SELECT DISTINCT
           DATEADD(
                      HOUR,
                      DATEPART(hh,
                               ISNULL(
                               (
                                   SELECT MAX(hora_hasta)
                                   FROM dbo.JDP_TUR_HORARIOS_UN_SERVICIO
                                   WHERE id_un_servicio = @id_un_servicio
                                         AND dia_semana = HOS.dia_semana
                                         AND hora_hasta < HOS.hora_desde
                               ),
                               '00:00:00.000000'
                                     )
                              ),
                      CONVERT(DATETIME, CONVERT(DATE, FIN.Fecha))
                  ) fecha_hora_desde,
           DATEADD(HOUR, DATEPART(HOUR, hora_desde), CONVERT(DATETIME, CONVERT(DATE, FIN.Fecha))) fecha_hora_hasta
    FROM dbo.JDP_TUR_HORARIOS_UN_SERVICIO HOS
        INNER JOIN dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
            ON HOS.id_un_servicio = SUN.id
        INNER JOIN dbo.JDP_TUR_SERVICIO SER
            ON SER.id = SUN.ID_servicio
        INNER JOIN dbo.JDP_TUR_UNIDAD_NEGOCIO UNE
            ON UNE.id_unidad_negocio = SUN.ID_unidad_negocio
        INNER JOIN #HorariosBloqueados FIN
            ON FIN.DiaSemana = HOS.dia_semana
        INNER JOIN dbo.JDP_TUR_ESTADOS_GRILLA ESG
            ON ESG.Estado = 1003
    WHERE SUN.ID_servicio = @Servicio
          AND UNE.id_unidad_negocio = @Unidad
          AND DATEDIFF(HOUR, FORMAT(DATEADD(hh, 0, '00:00:00'), 'HH:mm:ss'), hora_desde) > 0
    UNION ALL
    SELECT DISTINCT
           DATEADD(
                      HOUR,
                      DATEPART(hh,
                               ISNULL(
                               (
                                   SELECT MAX(hora_hasta)
                                   FROM dbo.JDP_TUR_HORARIOS_UN_SERVICIO
                                   WHERE id_un_servicio = @id_un_servicio
                                         AND dia_semana = HOS.dia_semana
                               ),
                               '00:00:00.000000'
                                     )
                              ),
                      CONVERT(DATETIME, CONVERT(DATE, FIN.Fecha))
                  ) fecha_hora_desde,
           CONVERT(DATETIME, CONVERT(DATE, FIN.Fecha)) + CONVERT(DATETIME, '23:59:59') fecha_hora_hasta
    FROM dbo.JDP_TUR_HORARIOS_UN_SERVICIO HOS
        INNER JOIN dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
            ON HOS.id_un_servicio = SUN.id
        INNER JOIN dbo.JDP_TUR_SERVICIO SER
            ON SER.id = SUN.ID_servicio
        INNER JOIN dbo.JDP_TUR_UNIDAD_NEGOCIO UNE
            ON UNE.id_unidad_negocio = SUN.ID_unidad_negocio
        INNER JOIN #HorariosBloqueados FIN
            ON FIN.DiaSemana = HOS.dia_semana
        INNER JOIN dbo.JDP_TUR_ESTADOS_GRILLA ESG
            ON ESG.Estado = 1003
    WHERE SUN.ID_servicio = @Servicio
          AND UNE.id_unidad_negocio = @Unidad
          AND DATEDIFF(HOUR, hora_hasta, FORMAT(DATEADD(hh, 0, '23:59:59'), 'hh:mm tt')) > 0
          AND DATEDIFF(HOUR, hora_desde, hora_hasta) > 0
) GRUPO
WHERE GRUPO.fecha_hora_desde < @FechaHasta
      AND GRUPO.fecha_hora_hasta > @FechaDesde;

