CREATE PROCEDURE [dbo].[JDP_TUR_SpClienteCRMFiltrado] @filtro VARCHAR(100)
AS

--DECLARE @filtro VARCHAR(100) ='94144224'
SELECT TOP 10
       cl.ID_Cliente AS id,
       cl.RazonSocial,
       cl.NombreFantasia,
       cl.CUIT AS documento,
       CONCAT(
                 'ID: ',
                 CAST(cl.ID_Cliente AS VARCHAR(MAX)) COLLATE DATABASE_DEFAULT,
                 ' - ',
                 ISNULL(cl.NombreFantasia, '') + ', ' COLLATE DATABASE_DEFAULT,
                 cl.RazonSocial COLLATE DATABASE_DEFAULT,
                 ' - ',
                 'DOC: ',
                 cl.CUIT COLLATE DATABASE_DEFAULT
             ) AS [text]
FROM dbo.CRM_CLIENTES cl
WHERE cl.FECHA_INHA IS NULL
      AND cl.ID_Cliente <> 1
      AND
      (
          cl.Marca1 = 1
          OR cl.Marca5 = 1
      )
      AND CONCAT(
                    CAST(cl.ID_Cliente AS VARCHAR(20)) COLLATE DATABASE_DEFAULT,
                    cl.NombreFantasia COLLATE DATABASE_DEFAULT,
                    cl.RazonSocial COLLATE DATABASE_DEFAULT,
                    cl.CUIT COLLATE DATABASE_DEFAULT
                ) LIKE CONCAT('%', @filtro COLLATE DATABASE_DEFAULT, '%');
