        SET @GrillaServiciosAdicionalesHTML
            = N'Servicios Adicionales:' + N'<table border="1">' + N'<tr><th>ID</th>'
              + N'<th>servicio</th><th>cantidad</th>' + N'<th>observaciones</th>'
              + CAST(
