/****** Object:  StoredProcedure [dbo].[JDP_TUR_SpGrillaTurnos2]    Script Date: 25/5/2023 03:29:24 ******/
USE [JDP];
GO

SET ANSI_NULLS ON;
GO
SET QUOTED_IDENTIFIER ON;
GO

----------------------
-- Modificado por JJGB
----------------------

CREATE PROCEDURE [dbo].[JDP_TUR_SpGrillaTurnos2]
(
    @unidad INT,
    @servicio INT,
    @desde DATETIME,
    @hasta DATETIME
)
AS
/*
DECLARE @unidad INT = 27,
        @servicio INT = 1,
        @desde DATETIME = '2023/4/24 1:37',
        --    @hasta DATETIME ='2023/4/25 1:37'
        @hasta DATETIME = '2023/6/6 1:37'
		
		
*/

DECLARE @unidad_new INT = @unidad;
DECLARE @servicio_new INT = @servicio;





SET NOCOUNT ON;

DECLARE @VariableAleatoria INT
    = ABS(CHECKSUM(NEWID())),
        @Dias INT = ((DATEPART(mm, @hasta) * 30) + DATEPART(dd, @hasta))
                    - ((DATEPART(mm, @desde) * 30) + DATEPART(dd, @desde)),
        @Incremento INT = 0,
        @id_un_servicio INT =
        (
            SELECT id
            FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
            WHERE SUN.ID_servicio = @servicio_new
                  AND SUN.ID_unidad_negocio = @unidad_new
        );

-- Busco temporal y la elimino x las dudas
IF OBJECT_ID('tempdb..#HorariosBloqueados') IS NOT NULL
    DROP TABLE #HorariosBloqueados;
--Creo una temporal con Fechas inhabilitadas  esto no se esta usando , a futuro podria ser util, revisar.
CREATE TABLE #HorariosBloqueados
(
    Clave INT,
    Fecha DATETIME,
    DiaSemana INT
);

WHILE (@Incremento <= @Dias)
BEGIN
    INSERT INTO #HorariosBloqueados
    (
        Clave,
        Fecha,
        DiaSemana
    )
    VALUES
    (@VariableAleatoria, DATEADD(D, @Incremento, @desde), DATEPART(dw, DATEADD(D, @Incremento, @desde)));
    SET @Incremento = @Incremento + 1;
END;

SET DATEFIRST 1;




---- invoco tres selects (turnos, turnos inhabilitados, turnos ocupados por otro servicio)
----  Este Select es de los turnos propiamente dicho    -----

--(1)
SELECT s.descripcion AS servicio,
       u.descripcion AS unidad_negocio, --TUR.*,
                                        --	   /*
       TUR.id_turnos,
       SUN.ID_servicio id_servicio,
       SUN.ID_unidad_negocio id_unidad_negocio,
       TUR.fecha_hora_alta,
       TUR.fecha_hora_desde,
       TUR.fecha_hora_hasta,
       TUR.vigencia_turno,
       TUR.id_sectorSolicitante,
       TUR.id_cliente,
       TUR.id_fallecido,
       TUR.id_presupuesto,
       TUR.id_version,
       TUR.aprobado_destino,
       TUR.observaciones,               --*/
       ISNULL(cl.Cod_Tango, '') AS cod_client,
       ISNULL(cl.NombreFantasia + ', ', '') + ISNULL(cl.RazonSocial, '') AS NombreCliente,
       CONCAT(
                 'ID: ',
                 CAST(cl.ID_Cliente AS VARCHAR(MAX)) COLLATE DATABASE_DEFAULT,
                 ' - ',
                 ISNULL(cl.NombreFantasia, '') + ', ' COLLATE DATABASE_DEFAULT,
                 cl.RazonSocial COLLATE DATABASE_DEFAULT,
                 ' - ',
                 'DOC: ',
                 cl.CUIT COLLATE DATABASE_DEFAULT
             ) AS razon_social,
       ISNULL(fa.ID_Cliente, '') AS cod_fallecido,
       ISNULL(fa.NombreFantasia + ', ', '') + ISNULL(fa.RazonSocial, '') AS fallecido,
       ISNULL(TUR.id_presupuesto, '') AS nro_presupuesto,
       ISNULL(TUR.id_presupuesto, '') AS cod_presupuesto,
       ISNULL(TUR.id_presupuesto, '') AS presupuesto,
       ISNULL(TUR.id_version, '') AS version,
       tet.color_pantalla AS color,
       te.id_estado AS estado,
       tet.descripcion AS descripcion_estado,
       TUR.id_sectorSolicitante sectorSolicitante,
       'ID:' + CONVERT(VARCHAR, SS.id) + ': ' + SS.descripcion + ' - ( ' + SUC.descripcion + ' )' descripcion_sector,
       te.fecha_hora ultima_actualizacion,
       CASE
           WHEN p.ID_Presupuesto IS NOT NULL THEN
               CONCAT(
                         'Nro: ',
                         p.ID_Presupuesto,
                         ' - Fecha Serv: ',
                         CONVERT(VARCHAR, p.CA_FECHA_SF, 103),
                         ' - ID Fall: ',
                         p.CA_FALLECIDO COLLATE DATABASE_DEFAULT,
                         ' - Fallecido: ',
                         ISNULL(fa.NombreFantasia + ', ', '') + ISNULL(fa.RazonSocial, '')COLLATE DATABASE_DEFAULT,
                         ' - Nro Serv: ',
                         p.CA_NRO_SERV_FUN
                     )
           ELSE
               'Seleccione.'
       END [text_pres],
       CASE
           WHEN p.ID_Presupuesto IS NOT NULL THEN
               CONCAT(
                         'Nro Version: ',
                         p.[Version],
                         ' - Fecha Serv: ',
                         CONVERT(VARCHAR, p.CA_FECHA_SF, 103),
                         ' - ID Fall: ',
                         p.CA_FALLECIDO COLLATE DATABASE_DEFAULT,
                         ' - Fallecido: ',
                         ISNULL(fa.NombreFantasia + ', ', '') + ISNULL(fa.RazonSocial, '')COLLATE DATABASE_DEFAULT,
                         ' - Nro Serv: ',
                         p.CA_NRO_SERV_FUN
                     )
           ELSE
               'Seleccione.'
       END [text_version],
       ISNULL(SUN.duracion_minima_turno, '00:30:00.0000000') duracion_minima_turno,
       TUR.id_un_servicio,
       0 Bloquea
FROM JDP_TUR_TURNOS TUR
    LEFT JOIN dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
        ON SUN.id = TUR.id_un_servicio
    LEFT JOIN JDP_TUR_SERVICIO s
        ON s.id_servicio = SUN.ID_servicio
    LEFT JOIN JDP_TUR_UNIDAD_NEGOCIO u
        ON u.id_unidad_negocio = SUN.ID_unidad_negocio
    LEFT JOIN
    (
        SELECT et.*
        FROM JDP_TUR_ESTADO_TURNO et
            INNER JOIN
            (
                SELECT id_turno,
                       MAX(fecha_hora) AS fecha_hora
                FROM JDP_TUR_ESTADO_TURNO
                GROUP BY id_turno
            ) AS tur
                ON et.id_turno = tur.id_turno
