CREATE PROCEDURE [dbo].[JDP_TUR_SpTurnosInhablitados]
    @Unidad INT,
    @Servicio INT,
    @FechaDesde DATETIME,
    @FechaHasta DATETIME
AS
----------------------
-- Modificado por JJGB
-- Creo no se usa este SP, hay que ver si tiene utilidad para bloquear dias en caso de emergencia
----------------------

DECLARE @VariableAleatoria INT;
SET @VariableAleatoria = ABS(CHECKSUM(NEWID()));
DECLARE @Dias INT;
SET @Dias
    = ((DATEPART(mm, @FechaHasta) * 30) + DATEPART(dd, @FechaHasta))
      - ((DATEPART(mm, @FechaDesde) * 30) + DATEPART(dd, @FechaDesde));
DECLARE @Incremento INT;
SET @Incremento = 0;

-- genero tabla temporal
IF OBJECT_ID('tempdb..#JDP_TUR_FECHASINHABILITADAS') IS NOT NULL
    DROP TABLE #JDP_TUR_FECHASINHABILITADAS;
CREATE TABLE #JDP_TUR_FECHASINHABILITADAS
(
    Clave INT,
    Fecha DATETIME
);

WHILE (@Incremento <= @Dias)
BEGIN
    INSERT INTO #JDP_TUR_FECHASINHABILITADAS
    (
        Clave,
        Fecha
    )
    VALUES
    (@VariableAleatoria, DATEADD(D, @Incremento, @FechaDesde));
    SET @Incremento = @Incremento + 1;
END;

SET DATEFIRST 1;

SELECT S.descripcion AS servicio,
       un.descripcion AS unidad_negocio,
       0 AS id_turnos,
       @Servicio AS id_servicio,
       @Unidad AS id_unidad_negocio,
       r.fecha_hora_desde,
       r.fecha_hora_hasta,
       NULL AS vigencia_turno,
       NULL AS id_cliente,
       NULL AS id_fallecido,
       NULL AS id_presupuesto,
       NULL AS id_version,
       NULL AS aprobado_destino,
       NULL AS observaciones,
       NULL AS cod_client,
       NULL AS razon_social,
       NULL AS cod_fallecido,
       NULL AS fallecido,
       NULL AS nro_presupuesto,
       NULL AS cod_presupuesto,
       NULL AS presupuesto,
       NULL AS version,
       '#000000' AS color,
       eg.Estado AS estado
FROM
(
    SELECT DISTINCT
           (fi.Fecha + '00:00') AS fecha_hora_desde,
           (fi.Fecha + CONVERT(VARCHAR(20), HS.hora_desde, 108)) AS fecha_hora_hasta
    FROM #JDP_TUR_FECHASINHABILITADAS AS fi
        INNER JOIN JDP_TUR_HORARIOS_SERVICIOS AS HS
            ON HS.dia_semana = DATEPART(dw, fi.Fecha)
    WHERE HS.hora_desde <> '00:00'
          AND HS.id_servicio = @Servicio
    UNION ALL
    SELECT DISTINCT
           (fi.Fecha + CONVERT(VARCHAR(20), HS.hora_hasta, 108)) AS fecha_hora_desde,
           (fi.Fecha + '23:59') AS fecha_hora_hasta
    FROM #JDP_TUR_FECHASINHABILITADAS AS fi
        INNER JOIN JDP_TUR_HORARIOS_SERVICIOS AS HS
            ON HS.dia_semana = DATEPART(dw, fi.Fecha)
    WHERE HS.hora_hasta <> '23:59'
          AND HS.id_servicio = @Servicio
) AS r
    LEFT JOIN JDP_TUR_SERVICIO S
        ON S.id_servicio = @Servicio
    LEFT JOIN JDP_TUR_UNIDAD_NEGOCIO un
        ON un.id_unidad_negocio = @Unidad
    LEFT JOIN JDP_TUR_ESTADOS_GRILLA eg
        ON eg.Tipo = 'H'
ORDER BY r.fecha_hora_desde ASC;

