    -- Cargo los servicios adicionales, si existen, en una grilla. sino dejo la variable en blanco
    IF EXISTS
    (
        SELECT 1
        FROM JDP_TUR_TIPO_UNIDAD_SERVICIOS_ADICIONALES tipo
            INNER JOIN dbo.JDP_TUR_TIPO_SERVICIOS_ADICIONALES TSA
                ON TSA.id_tipo_servicios_adicionales = tipo.id_tipo_servicios_adicionales
            LEFT JOIN JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES turno
                ON tipo.id_tipo_unidad_servicios_adicionales = turno.id_tipo_unidad_servicio_adicional
                   AND turno.id_turno = @turno
        WHERE tipo.id_unidad = @unidad
              AND TSA.id_servicio = @servicio
              AND tipo.habilitado = 1
              AND TSA.habilitado = 1
              AND ISNULL(turno.cantidad, 0) > 0
    )
        SET @GrillaServiciosAdicionalesHTML
            = N'Servicios Adicionales:' + N'<table border="1">' + N'<tr><th>ID</th>'
              + N'<th>servicio</th><th>cantidad</th>' + N'<th>observaciones</th>'
              + CAST(
                (
                    SELECT td = tipo.id_tipo_unidad_servicios_adicionales,
                           '',
                           td = TSA.descripcion,
                           '',
                           td = turno.cantidad,
                           '',
                           td = ISNULL(turno.observaciones, ''),
                           ''
                    FROM JDP_TUR_TIPO_UNIDAD_SERVICIOS_ADICIONALES tipo
                        INNER JOIN dbo.JDP_TUR_TIPO_SERVICIOS_ADICIONALES TSA
                            ON TSA.id_tipo_servicios_adicionales = tipo.id_tipo_servicios_adicionales
                        LEFT JOIN JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES turno
                            ON tipo.id_tipo_unidad_servicios_adicionales = turno.id_tipo_unidad_servicio_adicional
                               AND turno.id_turno = @turno
                    WHERE tipo.id_unidad = @unidad
                          AND TSA.id_servicio = @servicio
                          AND tipo.habilitado = 1
                          AND TSA.habilitado = 1
