              + N'*  -  _POR FAVOR NO CONTESTAR ESTE MENSAJE - INGRESAR A LA AGENDA WEB DE TURNOS PARA CUALQUIER MODIFICACION_';
        EXEC dbo.JDP_TUR_SpEnviaWS @telefono = @Telefono, -- varchar(max)
