    @Usuario INT,  
    @ROL INT,  
    @SUCURSALES XML,  
    @SERVICIOS XML,  
 @TIPOSUCURSAL XML  
AS  
BEGIN  
	-- Usa Nomenclatura JARDIN DEL PILAR    JDP_TUR
	-- SET NOCOUNT ON added to prevent extra result sets from  
    -- interfering with SELECT statements.  
    SET NOCOUNT ON;  
  
    INSERT INTO dbo.JDP_TUR_ROLES_USUARIOS  
