CREATE PROCEDURE [dbo].[JDP_TUR_SpGrabarTurno]
    -- Add the parameters for the stored procedure here
    @turno INT,
    @desde DATETIME,
    @hasta DATETIME,
    @unidad INT,
    @servicio INT,
    @cliente INT,
    @presupuesto INT,
    @version INT,
    @estado INT,
    @observaciones VARCHAR(MAX),
    @usuario VARCHAR(50),
    @fallecido INT,
    @sectorSolicitante INT,
    @adicionales XML,
    @id_un_servicio INT = NULL
AS
BEGIN
    -- SET NOCOUNT ON added to prevent extra result sets from
    -- interfering with SELECT statements.

    /*
DECLARE @turno INT = 0,
@desde DATETIME = '2023/05/26 02:00:00',
@hasta DATETIME = '2023/05/26 03:30:00',
@unidad INT = '27',
@servicio INT = '1',
@cliente INT = '94152533',
@presupuesto INT = NULL,
@version INT = NULL,
@estado INT = NULL,
@observaciones VARCHAR(MAX) = NULL,
@usuario VARCHAR(50) = 'JBAU',
@fallecido INT = NULL,
@sectorSolicitante INT = '15',
@adicionales XML = '<?xml version="1.0"?><datos/>',
@id_un_servicio INT = NULL
*/

    SET NOCOUNT ON;

    -- Insert statements for procedure here
    DECLARE @resp VARCHAR(MAX) = '',
            @identico BIT = 1,
            @unidad_new INT,
            @servicio_new INT;


    BEGIN TRANSACTION;
    BEGIN TRY
        --DECLARE @Vigencia int
        --SELECT @Vigencia = vigencia_reserva from JDP_TUR_servicio_unidad_negocio where id_unidad_negocio = @unidad_new AND id_servicio = @servicio_new
        -- TUVE QUE DEJAR LA REASIGNACION DEL ID_UN_SERVICIO, PORQUE KLAUSS SIGUE SIN MAR ESTE ID CUANDO ES NUEVO. IMAGINO QUE ESTA TOMANDO EL ID DE LA GRILLA
        -- NO LA GLOBAL
        IF (@id_un_servicio IS NULL)
            SET @id_un_servicio =
        (
            SELECT id
            FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
            WHERE SUN.ID_servicio = @servicio
                  AND SUN.ID_unidad_negocio = @unidad
        )   ;
        -- Cargo el ID de Servicio e ID de UN, cuando Klauss cambie, no va a venir mas en la llamada, cambie los nombre enb la llamda, para estar seguro no uso las que vienen.
        SELECT @unidad_new = SUN.ID_unidad_negocio,
               @servicio_new = SUN.ID_servicio
        FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
        WHERE id = @id_un_servicio;
        ------------------------------------------------------------------------------------------------------------------------------------------
        IF (@turno = 0)
        BEGIN
            SET @identico = 0;
            -- Si la primera vez que lo grabo el turno viene vacio o con cero, le pongo el primer estado disponible para esa unidad de negocio - Servicio
            IF (ISNULL(@estado, 0) = 0)
                SELECT @estado = ISNULL(
                                 (
                                     SELECT TOP 1
                                            FET.estado_siguiente
                                     FROM dbo.JDP_TUR_DEF_FLUJO_ESTADO DFE
                                         INNER JOIN dbo.JDP_TUR_FLUJO_ESTADO_TURNO FET
                                             ON FET.id_flujo_estado = DFE.id_flujo_estado
                                         INNER JOIN dbo.JDP_TUR_UN_SER_ROL UNSR
                                             ON UNSR.id_tipo_rol = DFE.rol_habilitado
                                         INNER JOIN Diccionario_048006_003.dbo.users US
                                             ON US.name = @usuario
                                         INNER JOIN dbo.JDP_TUR_ROLES_USUARIOS RU
                                             ON RU.id_usuario = US.id
                                                AND RU.id_unidad_negocio = @unidad_new
                                                AND RU.id_servicio = @servicio_new
                                     WHERE UNSR.id_unidad_negocio = @unidad_new
                                           AND UNSR.id_servicio = ISNULL(@servicio_new, 1)
                                           AND UNSR.id_tipo_rol = RU.id_tipo_rol
                                           AND FET.estado_inicial = 0
                                 ),
                                 ISNULL(
                                 (
                                     SELECT TOP 1
                                            estado_inicio
                                     FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO
                                     WHERE ID_unidad_negocio = 10
                                           AND ID_servicio = 1
                                 ),
                                 1
                                       )
                                       );
            --------------  Aca termina la verificacion
            -- Aca empiezo a grabar el turno
            INSERT INTO dbo.JDP_TUR_TURNOS
            (
                id_un_servicio,
                --id_servicio,
                --id_unidad_negocio,
                fecha_hora_alta,
                fecha_hora_desde,
                fecha_hora_hasta,
                id_cliente,
                id_presupuesto,
                id_version,
                id_fallecido,
                observaciones,
                vigencia_turno,
                id_sectorSolicitante
            )
            VALUES
            --(@id_un_servicio, @servicio_new, @unidad_new, GETDATE(), @desde, @hasta, @cliente, @presupuesto, @version,
            (@id_un_servicio, GETDATE(), @desde, @hasta, @cliente, @presupuesto, @version, @fallecido, @observaciones,
             DATEADD(HH, 30, GETDATE()), @sectorSolicitante);
            SET @turno =
            (
                SELECT IDENT_CURRENT('JDP_TUR_TURNOS')
            );
            -- grabo el primer estado del turno
            INSERT INTO JDP_TUR_ESTADO_TURNO
            (
                id_turno,
                id_estado,
                fecha_hora,
                usuario
            )
            VALUES
            (@turno, @estado, GETDATE(), @usuario);
        END;
        ELSE IF NOT EXISTS -- Esto es para evitar sobre escribir el turno, si todos los datos son iguales.
             (
                 SELECT TOP 1
                        1
                 FROM JDP_TUR_TURNOS TUR
                     INNER JOIN
                     (
                         SELECT TOP 1
                                *
                         FROM JDP_TUR_ESTADO_TURNO
                         WHERE id_turno = @turno
                         ORDER BY fecha_hora DESC
                     ) EST
                         ON EST.id_turno = TUR.id_turnos
                 WHERE TUR.id_un_servicio = @id_un_servicio
                       --AND TUR.id_servicio = @servicio_new
                       --AND TUR.id_unidad_negocio = @unidad_new
                       AND TUR.fecha_hora_desde = @desde
                       AND TUR.fecha_hora_hasta = @hasta
                       AND TUR.id_cliente = @cliente
                       AND ISNULL(TUR.id_presupuesto, -1) = ISNULL(@presupuesto, -1)
                       AND ISNULL(TUR.id_version, -1) = ISNULL(@version, -1)
                       AND ISNULL(TUR.id_fallecido, -10) = ISNULL(@fallecido, -10)
                       AND ISNULL(TUR.observaciones, '') = ISNULL(@observaciones, '')
                       AND TUR.id_sectorSolicitante = ISNULL(@sectorSolicitante, 13)
                       AND TUR.id_turnos = @turno
                       AND EST.id_estado = @estado
             --AND EST.usuario = @usuario
             )
        BEGIN
            SET @identico = 0;
            UPDATE JDP_TUR_TURNOS
            SET id_un_servicio = @id_un_servicio,
                --id_servicio = @servicio_new,
                --id_unidad_negocio = @unidad_new,
                fecha_hora_desde = @desde,
                fecha_hora_hasta = @hasta,
                id_cliente = @cliente,
                id_presupuesto = @presupuesto,
                id_version = @version,
                id_fallecido = @fallecido,
                observaciones = @observaciones,
                vigencia_turno = DATEADD(HH, 30, GETDATE()),
                id_sectorSolicitante = @sectorSolicitante
            WHERE id_turnos = @turno;

            -- grabo el ultimo estado del turno
            INSERT INTO JDP_TUR_ESTADO_TURNO
            (
                id_turno,
                id_estado,
                fecha_hora,
                usuario
            )
            VALUES
            (@turno, @estado, GETDATE(), @usuario);
        END;
        -- Grabo los Campos  Adicionales
        -- Aca pongo mi Query par aactualizar y agregar o eliminar los Servicios Adicionales.
        SELECT c.value('id[1]', 'INT') AS id,
               c.value('servicio[1]', 'varchar(50)') AS servicio,
               c.value('cantidad[1]', 'INT') AS cantidad,
               c.value('observaciones[1]', 'varchar(MAX)') AS observaciones
        INTO #TEMP
        FROM @adicionales.nodes('/datos/data') AS x(c);

        --elimina los registro que ahora puse en cero
        --elimina los registro que ahora puse en cero
        DELETE FROM JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES
        WHERE id_turno = @turno
              AND id_tipo_unidad_servicio_adicional =
              (
                  SELECT id FROM #TEMP tempo WHERE tempo.cantidad = 0
              );
        -- Veo si se elimino para poder mandar el correo modificando la bandera
        IF @@ROWCOUNT > 0
            SET @identico = 0;
        --Solo hace update de los que ya estan en la tabla y que la cantidad sea mayor a 0.
        UPDATE JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES
        SET cantidad = tempo.cantidad,
            observaciones = tempo.observaciones
        FROM #TEMP tempo
            INNER JOIN JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES USA
                ON tempo.id = USA.id_tipo_unidad_servicio_adicional
                   AND USA.id_turno = @turno
                   AND tempo.cantidad > 0
        WHERE tempo.cantidad <> USA.cantidad
              OR tempo.observaciones <> USA.observaciones;

        -- Veo si se actualizao para poder mandar el correo modificando la bandera
        IF @@ROWCOUNT > 0
            SET @identico = 0;

        --inserta solo los que faltan en la tabla
        INSERT INTO dbo.JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES
        (
            id_turno,
            id_tipo_unidad_servicio_adicional,
            observaciones,
            cantidad
        )
        SELECT @turno,
               id,
               observaciones,
               cantidad
        FROM #TEMP
        WHERE #TEMP.cantidad > 0
              AND id NOT IN
                  (
                      SELECT tempo.id
                      FROM #TEMP tempo
                          INNER JOIN JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES USA
                              ON tempo.id = USA.id_tipo_unidad_servicio_adicional
                                 AND USA.id_turno = @turno
                                 AND tempo.cantidad > 0
                      WHERE tempo.cantidad = USA.cantidad
                            OR tempo.observaciones = USA.observaciones
                  );
        -- Veo si insertó para poder mandar el correo modificando la bandera
        IF @@ROWCOUNT > 0
            SET @identico = 0;
        DROP TABLE #TEMP;
        IF (@identico < 1) -- Aca evito que mande dos veces el mismo correo o sobre escriba 
        BEGIN
            /* -- Este es el query original de PQ
            -- Grabo los Campos  Adicionales
            SELECT c.value('id[1]', 'INT') AS id,
                   c.value('servicio[1]', 'varchar(50)') AS servicio,
                   c.value('cantidad[1]', 'INT') AS cantidad,
                   c.value('observaciones[1]', 'varchar(MAX)') AS observaciones
            INTO #TEMP
            FROM @adicionales.nodes('/datos/data') AS x(c);
			
            DELETE TSA
            FROM JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES TSA
                INNER JOIN #TEMP
                    ON TSA.id_turno = @turno
                       AND TSA.id_tipo_unidad_servicio_adicional = #TEMP.id
            WHERE #TEMP.cantidad = 0;

            INSERT INTO JDP_TUR_UNIDAD_SERVICIOS_ADICIONALES
            (
                id_turno,
                id_tipo_unidad_servicio_adicional,
                cantidad,
                observaciones
            )
            SELECT @turno,
                   #TEMP.id,
                   #TEMP.cantidad,
                   #TEMP.observaciones
            FROM #TEMP
            WHERE #TEMP.cantidad > 0;
			*/
            -- Mando el Correo
