CREATE PROCEDURE [dbo].[JDP_TUR_SpPresupuestos] @cliente INT
AS
SELECT DISTINCT
       c.ID_Presupuesto,
       c.CA_FECHA_SF AS Fecha,
       c.CA_FALLECIDO AS id_fallecido,
       fa.RazonSocial AS fallecido,
       c.CA_NRO_SERV_FUN AS servicio_funebre,
       c.ID_Presupuesto AS Id,
       CONCAT(
                 'Nro: ',
                 c.ID_Presupuesto,
                 ' - Fecha Serv: ',
                 CONVERT(VARCHAR, c.CA_FECHA_SF, 103),
                 ' - ID Fall: ',
                 c.CA_FALLECIDO COLLATE DATABASE_DEFAULT,
                 ' - Fallecido: ',
                 fa.NombreFantasia COLLATE DATABASE_DEFAULT,
                 ', ',
                 fa.RazonSocial COLLATE DATABASE_DEFAULT,
                 ' - Nro Serv: ',
                 c.CA_NRO_SERV_FUN
             ) AS [text]
FROM CRM_PRESUPUESTOS c
    LEFT JOIN CRM_CLIENTES fa
        ON c.CA_FALLECIDO = fa.ID_Cliente
           AND fa.Marca4 = 1
    LEFT JOIN CRM_CLIENTES cl
        ON c.ID_Cliente = cl.ID_Cliente
           AND cl.Marca1 = 1
    LEFT JOIN CRM_ESTADOSPresupuesto p
        ON c.ID_EstadoPresupuesto = p.ID_EstadoPresupuesto
WHERE c.ID_EstadoPresupuesto <> 'INA'
      AND c.ID_Cliente = @cliente
      AND
      (
          c.CA_FALLECIDO <> ''
          OR c.CA_FALLECIDO IS NOT NULL
      )
ORDER BY Fecha DESC;

