CREATE PROCEDURE [dbo].[JDP_TUR_SpTurnosOcupados]
    @unidad INT,
    @servicio INT,
    @Desde DATETIME,
    @Hasta DATETIME
AS

/*
DECLARE @desde DATETIME = '2023-05-25 10:00:00.000';
DECLARE @hasta DATETIME = '2023-05-25 23:00:00.000';
*/

DECLARE @unidad_new INT = 27; --@unidad
DECLARE @servicio_new INT = 1; --@servicio


SET NOCOUNT ON;

DECLARE @id_un_servicio INT =
        (
            SELECT id
            FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
            WHERE SUN.ID_servicio = @servicio_new
                  AND SUN.ID_unidad_negocio = @unidad_new
        );

--este SP es para ver que otros turnos tengo en el mismo horario de otros servicios
SELECT s.descripcion AS servicio,
       u.descripcion AS unidad_negocio,
       TUR.id_turnos,
       TUR.id_un_servicio,
       SUN.ID_servicio,
       SUN.ID_unidad_negocio,
       TUR.fecha_hora_alta,
       TUR.fecha_hora_desde,
       TUR.fecha_hora_hasta,
       TUR.vigencia_turno,
       TUR.id_sectorSolicitante,
       TUR.id_cliente,
       TUR.id_fallecido,
       TUR.id_presupuesto,
       TUR.id_version,
       TUR.aprobado_destino,
       TUR.observaciones,
       cl.Cod_Tango AS cod_client,
       cl.RazonSocial AS razon_social,
       fa.ID_Cliente AS cod_fallecido,
       fa.RazonSocial AS fallecido,
       p.ID_Presupuesto AS nro_presupuesto,
       p.ID_Presupuesto AS cod_presupuesto,
       p.ID_Presupuesto AS presupuesto,
       p.[Version] AS version,
       eg.color_pantalla AS color,
       eg.Estado AS estado
FROM JDP_TUR_TURNOS TUR
    LEFT JOIN dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
        ON SUN.id = TUR.id_un_servicio
    LEFT JOIN JDP_TUR_SERVICIO s
        ON s.id_servicio = SUN.ID_servicio
    LEFT JOIN JDP_TUR_UNIDAD_NEGOCIO u
        ON u.id_unidad_negocio = SUN.ID_unidad_negocio
    LEFT JOIN JDP_TUR_ESTADOS_GRILLA eg
        ON eg.Tipo = 'O'
    LEFT JOIN CRM_CLIENTES cl
        ON TUR.id_cliente = cl.ID_Cliente
    LEFT JOIN CRM_CLIENTES fa
        ON TUR.id_fallecido = cl.ID_Cliente
    LEFT JOIN CRM_PRESUPUESTOS p
        ON TUR.id_presupuesto = p.ID_Presupuesto
WHERE SUN.ID_unidad_negocio = @unidad_new
      AND SUN.ID_servicio <> @servicio_new
      AND TUR.fecha_hora_desde <= @Hasta
      AND TUR.fecha_hora_hasta >= @Desde;
