----------------------

CREATE PROCEDURE [dbo].[JDP_TUR_SPValidarTurno]
    -- Add the parameters for the stored procedure here
    @desde DATETIME,
    @hasta DATETIME,
    @unidad INT,
    @servicio INT,
    @cliente INT,
    @presupuesto INT,
    @version INT,
    @estado INT,
    @observaciones VARCHAR(MAX),
    @usuario VARCHAR(50),
    @turno INT,
    @ultima_actualizacion DATETIME, --= NULL, -- esto hay que cambiarlo cuando KLAUSS modifique el envio al SP
    @sectorSolicitante INT,
    @id_un_servicio INT

-- ,@adicionales XML
AS
BEGIN
    -- SET NOCOUNT ON added to prevent extra result sets from
    -- interfering with SELECT statements.
    SET NOCOUNT ON;
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------

    --DECLARO VARIABLES
    DECLARE @resp VARCHAR(MAX) = '',
            @cantidad_Inhabilitados INT = 0,
            @cantidad_turnos INT = 0,
            @maximo INT = 0,
            @antelacion TIME = '01:00:00', --TENEMOS ESTO RESUELTO?
            @ultima_actualizacion_disco DATETIME,
            @sectorSolicitante_disco INT,
            @ventanaEntreTurnos TIME,      -- Resolver
            @duracion_minima_turno TIME,
            @unidad_new INT,
            @servicio_new INT;

    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    --CARGO VARIABLES

    --Cargo la ultima actualizacion del turno en las tablas para no solaparme con un turno actualizado despues de que estoy editando
    --Le quito 3 horas a la hora que envia el FrontEnd por que la envia en hora ZULU y viene con 3 horas de mas

    --SET @ultima_actualizacion = DATEADD(HOUR, -3, @ultima_actualizacion);
    SELECT TOP 1
           @ultima_actualizacion_disco = EST.fecha_hora
    FROM dbo.JDP_TUR_ESTADO_TURNO EST
    WHERE EST.id_turno = @turno
    ORDER BY EST.fecha_hora DESC;
    --si no existe porque es un alta, pongo la que traigo para poder grabar.
    IF (@ultima_actualizacion_disco IS NULL)
        SET @ultima_actualizacion_disco = @ultima_actualizacion;

    -- Cargo el id de UN - Servicio, porque todavia no viene en la llamada. Klauss tiene que cambiar esto
    -- DEJO LA ASIGNACIOIN DE ABAJO PORQUE KLAUSS SIGUE SIN MANDAR EL ID CUANDO ES UN TURNO NUEVO
    IF (@id_un_servicio IS NULL)
        SET @id_un_servicio =
    (
        SELECT id
        FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
        WHERE SUN.ID_servicio = @servicio
              AND SUN.ID_unidad_negocio = @unidad
    )   ;

    -- Cargo el ID de Servicio e ID de UN, cuando Klauss cambie, no va a venir mas en la llamada, cambie los nombre en la llamada, para estar seguro no uso las que vienen.
    SELECT @unidad_new = SUN.ID_unidad_negocio,
           @servicio_new = SUN.ID_servicio
    FROM dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
    WHERE id = @id_un_servicio;
    ------------------------------------------------------------------------------------------------------------------------------------------

    --Cargo variables para verificar relativas a la UN - Servicio
    SELECT @maximo = sun.max_simultaneos,
           @antelacion = sun.antelacion_minima,
           @ventanaEntreTurnos = sun.ventanaEntreTurnos,
           @duracion_minima_turno = sun.duracion_minima_turno
    FROM JDP_TUR_SERVICIO_UNIDAD_NEGOCIO sun
    WHERE sun.id = @id_un_servicio
          AND sun.ID_unidad_negocio = @unidad_new;
    --
    -- Cargo la variable para saber si el horario esta habilitado
    EXEC [JDP_TUR_SpHorariosInhablitados] @unidad_new,
                                          @servicio_new,
                                          @id_un_servicio,
                                          @desde,
                                          @hasta,
                                          @cantidad_Inhabilitados OUTPUT;


    -- Cargo la variable @cantidad_turnos con la cantidad de turnos que hay en esa ventana horaria
    -- Ya compara bien y no se escapa en la superposicion con drag and drop.
    SELECT @cantidad_turnos = COUNT(*)
    FROM JDP_TUR_TURNOS TUR
        INNER JOIN
        (
            SELECT et.*
            FROM JDP_TUR_ESTADO_TURNO et
                INNER JOIN
                (
                    SELECT id_turno,
                           MAX(fecha_hora) AS fecha_hora
                    FROM JDP_TUR_ESTADO_TURNO
                    GROUP BY id_turno
                ) AS t
                    ON et.id_turno = t.id_turno
                       AND et.fecha_hora = t.fecha_hora
        ) AS estado
            ON TUR.id_turnos = estado.id_turno
        --ON estado.id_turno = @turno
        INNER JOIN dbo.JDP_TUR_TIPO_ESTADOS_TURNO EST
            ON EST.id_tipo_estados_turno = estado.id_estado
    WHERE (
              (
                  fecha_hora_desde <= @desde
                  AND fecha_hora_hasta > @desde
              )
              OR
              (
                  fecha_hora_desde >= @desde
                  AND fecha_hora_desde < @hasta
              )
          )
          AND TUR.id_un_servicio = @id_un_servicio
          AND id_turnos <> @turno
          AND EST.libera_grilla = 0;

    -- Cargo el sector en disco para verificar que solo autorizados modifiquen
    --si no existe porque es un alta, pongo la que traigo para poder grabar.
    SELECT @sectorSolicitante_disco = TUR.id_sectorSolicitante
    FROM JDP_TUR_TURNOS TUR
    WHERE id_turnos = @turno;
    IF (@sectorSolicitante_disco IS NULL)
        SET @sectorSolicitante_disco = @sectorSolicitante;

    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------
    -----------------------------------------------------------------------------------------------------------------------------------------------------------------

    -- Empiezo las verificaciones
    -- Verifico que el sector solicitante en disco, sea un sector autorizado para el usuariograbo con datos posteriores a la ultima version en disco
    IF NOT EXISTS
    (
        SELECT 1
        FROM JDP_TUR_USUARIO_SECTOR USEC
            INNER JOIN Diccionario_048006_003.dbo.users USU
                ON USU.id = USEC.id_usuario
        WHERE USEC.id_sector = @sectorSolicitante_disco
              AND USU.name = @usuario
    )
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - Ud. no posee derechos suficientes para modificar este turno.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );
    -- Verifico que no estoy grabando en un grupo de celdas que estan bloqueadas por el administrador

    IF EXISTS
    (
        SELECT 1
        FROM dbo.JDP_TUR_BLOQUEOS_UN_SERVICIO BSER
            INNER JOIN dbo.JDP_TUR_ESTADOS_GRILLA ESG
                ON ESG.Estado = BSER.id_estado_grilla
            INNER JOIN dbo.JDP_TUR_SERVICIO_UNIDAD_NEGOCIO SUN
                ON BSER.id_UN_Servicio = SUN.id
        WHERE SUN.ID_servicio = @servicio_new
              AND SUN.ID_unidad_negocio = @unidad_new
              AND
              (
                  (
                      BSER.fecha_hora_desde <= @desde
                      AND BSER.fecha_hora_hasta > @desde
                  )
                  OR
                  (
                      BSER.fecha_hora_desde >= @desde
                      AND BSER.fecha_hora_desde < @hasta
                  )
              )
    )
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - Esta intentado grabar un turno en un horario que ha sido bloqueado por el administrador. consulte con la sucursal.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );


    -- Verifico que grabo con datos posteriores a la ultima version en disco
    --	SELECT @resp
    --            = CONCAT(@ultima_actualizacion , @ultima_actualizacion_disco)

    IF (@ultima_actualizacion < @ultima_actualizacion_disco)
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - Los datos que esta intentando grabar ya han sido actualizados por otra persona, por favor cancele esta actualizacion y presione el boton ACTUALIZAR para cargar la version mas reciente del turno en la grilla.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );

    -- Verifico que la fecha sea mayor o igual al momento actual + la antelacion.
    IF (@desde < GETDATE())
        SELECT @resp
            = CONCAT(@resp, ' - El período es anterior a este momento.', CHAR(13), CHAR(10), CHAR(13), CHAR(10));

    -- Verifico que el turno no empiece antes de la antelacion determinada.
    IF (@desde < GETDATE() + CONVERT(DATETIME, @antelacion))
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - No puede iniciar un turno con una antelación mínima menor a: ',
                        CONVERT(VARCHAR, @antelacion, 8),
                        ' horas.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );

    -- Verifico que el inicio no sea mayor que fin
    IF (@desde > @hasta)
        SELECT @resp = CONCAT(@resp, ' - El inicio es mayor al fin.', CHAR(13), CHAR(10), CHAR(13), CHAR(10));

    /*IF (ISNULL(@estado, 0) = 0)
        SELECT @resp = CONCAT(@resp, ' - Falta ingresar el estado');*/

    -- Verifico que tenga Cliente
    IF (ISNULL(@cliente, 0) = 0)
        SELECT @resp = CONCAT(@resp, ' - Falta ingresar el cliente.', CHAR(13), CHAR(10), CHAR(13), CHAR(10));

    -- Verifico que tenga presupuesto si estado es 3
    IF (ISNULL(@presupuesto, 0) = 0 AND @estado = 3) -- Solo tengo que tener presupuesto cuando confirmo el turno
        SELECT @resp = CONCAT(@resp, ' - Falta ingresar el presupuesto.', CHAR(13), CHAR(10), CHAR(13), CHAR(10));

    --Verifico que tenga version, si ingrese presupuesto.
    IF (ISNULL(@version, -1) = -1 AND ISNULL(@presupuesto, 0) <> 0) --So cargue presupuesto, elegime la version
        SELECT @resp = CONCAT(@resp, ' - Falta ingresar la versión.', CHAR(13), CHAR(10), CHAR(13), CHAR(10));

    -- Verifico que el horario este habilitado para realizar una reserva
    IF (ISNULL(@cantidad_Inhabilitados, 0) <> 0)
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - Horario inhabilitado para esta unidad y servicio.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );

    IF (@sectorSolicitante IS NULL)
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - Es necesario indique el sector que esta solicitando el turno, por favor seleccione de la lista desplegable y reintente.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );

    --Verifico la duracion minima de un turno
    IF (@desde + CONVERT(DATETIME, @duracion_minima_turno) > @hasta)
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - El turno debe tener una duración mínima de :',
                        CONVERT(VARCHAR, @duracion_minima_turno, 8),
                        ' horas.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );
    --Verifico la ventana entre los turnos
    IF EXISTS
    (
        SELECT 1
        FROM JDP_TUR_TURNOS TUR
            INNER JOIN
            (
                SELECT et.*
                FROM JDP_TUR_ESTADO_TURNO et
                    INNER JOIN
                    (
                        SELECT id_turno,
                               MAX(fecha_hora) AS fecha_hora
                        FROM JDP_TUR_ESTADO_TURNO
                        GROUP BY id_turno
                    ) AS t
                        ON et.id_turno = t.id_turno
                           AND et.fecha_hora = t.fecha_hora
            ) AS estado
                ON TUR.id_turnos = estado.id_turno
            --ON estado.id_turno = @turno
            INNER JOIN dbo.JDP_TUR_TIPO_ESTADOS_TURNO EST
                ON EST.id_tipo_estados_turno = estado.id_estado
        WHERE fecha_hora_desde <= @desde
              AND fecha_hora_hasta + CONVERT(DATETIME, @ventanaEntreTurnos) > @desde
              AND TUR.id_un_servicio = @id_un_servicio
              --AND id_servicio = @servicio_new
              --AND id_unidad_negocio = @unidad_new
              AND id_turnos <> @turno
              AND EST.libera_grilla = 0
    )
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - No puede solicitar un turno con menos de  ',
                        CONVERT(VARCHAR, @ventanaEntreTurnos, 8),
                        ' horas antes del turno anterior.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );

    --Verifico que NO se solapen los turnos
    IF (@cantidad_turnos >= @maximo)
        SELECT @resp
            = CONCAT(
                        @resp,
                        ' - Superó la cantidad máxima de ' + CONVERT(VARCHAR, @maximo)
                        + ' turnos posibles dentro de la misma ventana horaria.',
                        CHAR(13),
                        CHAR(10),
                        CHAR(13),
                        CHAR(10)
                    );

    ---- Cierro y envio la respuesta concatenada.
    SELECT @resp AS respuesta;
END;

